import React, { Component } from "react";
import Enfant from "./Enfant";

export const MyContext = React.createContext("ValeurparDéfaut");
class App extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <MyContext.Provider value="Robin">
        <Enfant />
        <Enfant />
        <Enfant />
      </MyContext.Provider>
    );
  }
}

export default App;
