import React, { Component } from "react";
import styled from "styled-components";

export const NavbarLeftStyle = styled.aside`
  position: absolute;
  width: 240px;
  transform: translateX(0px);
  top: 0;
  bottom: 0;
  box-shadow: 0 12px 25px rgba(0, 0, 0, 0.1);
  z-index: 100;
  transition: all 0.3s ease-in-out;
  &.inactive {
    display: none;
    transform: translateX(-240px);
  }
`;

export const LogoContainer = styled.div`
  width: 100%;
  padding: 16px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  background: #414345;
  .brand-name {
    font-weight: 200;
    font-size: 1em;
    text-align: right;
    color: #ffffff;
  }
  .material-icons {
    color #ffffff;
    &:hover {
      cursor: pointer
    }
  }
`;
